mapper.select:
SELECT a.areaid AS areaid , Right(a.areaNo, 4) AS areaNo , a.registeredNo AS registeredNo , a.name AS name ,n.name||'-'||a.addr AS addr ,case when a.DS='S' then '单月' when a.DS='D' then '双月' end AS ds ,COUNT(m.meterid) AS zBs ,SUM(m.TH_NUMBER) AS zDs ,SUM(m.S_NUMBER) AS bqyl ,t.n AS blbs ,a.reserved AS reserved ,MAX(m.TH_R_TIME) AS zjcssj ,a.lastsumday AS lastsumDay ,MIN(m.starttime) AS zzzbsj , a.description AS description FROM ha_area a LEFT JOIN ha_meter m ON m.areaid=a.areaid LEFT JOIN ha_rgn n ON n.id=Left(a.areaNo, 1) LEFT JOIN ( SELECT Count(i.meterid) AS n,i.areaid AS areaid FROM hav_meterinfo i WHERE i.状态!='正常' AND i.状态!='正确' AND i.状态!='建档' AND i.状态!='集中器无返回' AND i.状态!='关阀' AND i.状态!='修改' AND i.状态!='已更正' AND i.状态!='已更换' AND i.状态!='采集器无返回' AND i.状态 NOT LIKE '%用量异常%' AND i.表号 >0 GROUP BY i.areaid) t ON t.areaid=a.areaid WHERE length(to_char(a.areaid,'9999'))>0
and left(a.areaNo,1) = #{params.rgnid}
and (a.areaNo like '%${params.keyWord}%' or a.registeredNo like '%${params.keyWord}%' or a.name like '%${params.keyWord}%' or n.name||'-'||a.addr like '%${params.keyWord}%' or to_char(m.TH_R_TIME,'yyyy-MM-dd') like '%${params.keyWord}%' or to_char(a.lastsumday,'yyyy-MM-dd') like '%${params.keyWord}%' or case when a.DS='S' then '单月' when a.DS='D' then '双月' end = #{params.keyWord} )
and a.DS = #{params.monthType}
GROUP BY a.areaid,a.registeredNo,a.name,a.areaNo,n.name||'-'||a.addr,a.DS,a.description,a.reserved,a.lastsumday,t.n ORDER BY a.areaNo

SELECT a.areaid AS areaid, Right(a.areaNo, 4) AS aareaNo, a.registeredNo AS aregisteredNo, a.name AS aname,concat_ws('_',n.name,a.addr) AS aaddr, case a.DS when 'S' then '单月' else '双月' end AS ads, COUNT(m.meterid) AS mmeterid, SUM(m.TH_NUMBER) AS mTH_NUMBER, SUM(m.S_NUMBER) AS mS_NUMBER, t.n AS atnNumber, a.reserved AS areserved, to_date(to_char(GREATEST(m.TH_R_TIME),'YYYY-MM-DD hh:mm:ss'),'YYYY-MM-DD hh:mm:ss') AS mTH_R_TIME, to_date(to_char(a.lastsumday,'YYYY-MM-DD hh:mm:ss'),'YYYY-MM-DD hh:mm:ss') AS alastsumday, to_date(to_char(LEAST(m.starttime),'YYYY-MM-DD hh:mm:ss'),'YYYY-MM-DD hh:mm:ss') AS mstarttime, a.description AS adescription FROM ((ha_area a LEFT JOIN ha_meter m ON m.areaid=a.areaid) LEFT JOIN ha_rgn n ON n.id=Left(a.areaNo, 1)) LEFT JOIN (SELECT Count(i.meterid) AS n,i.areaid AS areaid FROM hav_meterinfo i WHERE i.状态!='正常' AND i.状态!='正确' AND i.状态!='建档' AND i.状态!='集中器无返回' AND i.状态!='关阀' AND i.状态!='修改' AND i.状态!='已更正' AND i.状态!='已更换' AND i.状态!='采集器无返回' AND i.状态 NOT LIKE '%用量异常%' AND i.表号 >0 GROUP BY i.areaid) t ON t.areaid=a.areaid group by a.areaid,a.areaNo,a.registeredNo,a.name,n.name,a.addr,a.DS,t.n, a.reserved,m.TH_R_TIME, a.lastsumday,m.starttime,a.description
and ds = #{HaArea.ds}
and a.areaid in
#{item}
and (a.areaNo like '%${HaArea.keyWord}' or a.registeredNo like '%${HaArea.keyWord}%' or a.name like '%${HaArea.keyWord}%')
ORDER BY a.areaNo limit #{page} OFFSET #{rowNum}

SELECT a.areaid AS areaid, Right(a.areaNo, 4) AS aareaNo, a.registeredNo AS aregisteredNo, a.name AS aname,concat_ws('_',n.name,a.addr) AS aaddr, case a.DS when 'S' then '单月' else '双月' end AS ads, COUNT(m.meterid) AS mmeterid, SUM(m.TH_NUMBER) AS mTH_NUMBER, SUM(m.S_NUMBER) AS mS_NUMBER, t.n AS atnNumber, a.reserved AS areserved, to_date(to_char(GREATEST(m.TH_R_TIME),'YYYY-MM-DD hh:mm:ss'),'YYYY-MM-DD hh:mm:ss') AS mTH_R_TIME, to_date(to_char(a.lastsumday,'YYYY-MM-DD hh:mm:ss'),'YYYY-MM-DD hh:mm:ss') AS alastsumday, to_date(to_char(LEAST(m.starttime),'YYYY-MM-DD hh:mm:ss'),'YYYY-MM-DD hh:mm:ss') AS mstarttime, a.description AS adescription FROM ((ha_area a LEFT JOIN ha_meter m ON m.areaid=a.areaid) LEFT JOIN ha_rgn n ON n.id=Left(a.areaNo, 1)) LEFT JOIN (SELECT Count(i.meterid) AS n,i.areaid AS areaid FROM hav_meterinfo i WHERE i.状态!='正常' AND i.状态!='正确' AND i.状态!='建档' AND i.状态!='集中器无返回' AND i.状态!='关阀' AND i.状态!='修改' AND i.状态!='已更正' AND i.状态!='已更换' AND i.状态!='采集器无返回' AND i.状态 NOT LIKE '%用量异常%' AND i.表号 >0 GROUP BY i.areaid) t ON t.areaid=a.areaid group by a.areaid,a.areaNo,a.registeredNo,a.name,n.name,a.addr,a.DS,t.n, a.reserved,m.TH_R_TIME, a.lastsumday,m.starttime,a.description
and ds = #{HaArea.ds}
and a.areaid in
#{item}
and (a.areaNo like '%${HaArea.keyWord}' or a.registeredNo like '%${HaArea.keyWord}%' or a.name like '%${HaArea.keyWord}%')

SELECT a.areaid AS areaId, Right(a.areaNo, 4) AS areaNo, trim(a.registeredNo) AS registeredNo, a.name AS name, trim(n.name||'-'||a.addr) AS addr, case when a.DS='S' then '单月' when a.DS='D' then '双月' end AS ds, COUNT(m.meterid) AS haMeterCount, SUM(m.TH_NUMBER) AS thNumber, SUM(m.S_NUMBER) AS sNumber, t.n AS 不良表数, trim(a.reserved) AS reserved, to_char(MAX(m.TH_R_TIME),'yyyy-mm-dd hh24:mi:ss') AS thrTime, trim(to_char(a.lastsumday,'yyyy-mm-dd hh24:mi:ss')) AS lastsumDay, to_char(MIN(m.starttime),'YYYY-MM-DD') AS startTime,a.description AS description FROM ha_area a LEFT JOIN ha_meter m ON m.areaid=a.areaid LEFT JOIN ha_rgn n ON n.id=Left(a.areaNo, 1) LEFT JOIN
(SELECT Count(i.meterid) AS n,i.areaid AS areaid FROM hav_meterinfo i WHERE i.状态!='正常' AND i.状态!='正确' AND i.状态!='建档' AND i.状态!='集中器无返回' AND i.状态!='关阀' AND i.状态!='修改' AND i.状态!='已更正' AND i.状态!='已更换' AND i.状态!='采集器无返回' AND i.状态 NOT LIKE '%用量异常%' AND i.表号 >0 GROUP BY i.areaid) as t
ON t.areaid=a.areaid WHERE length(trim(to_char(a.areaid,'9999999')))>0
and left(a.areaNo,1) = #{haArea.rgnIds}
and (a.registeredNo like '%${haArea.keyWord}%' or a.name like '%${haArea.keyWord}%' or a.areaNo like '%${haArea.keyWord}%')
and a.DS = #{haArea.monthType}
GROUP BY a.areaid,a.registeredNo,a.name,a.areaNo,n.name||'-'||a.addr, a.DS,a.description,a.reserved,to_char(a.lastsumday,'yyyy-mm-dd hh24:mi:ss'),t.n ORDER BY a.areaNo limit #{page} OFFSET #{rowNum}
