mapper.select:
SELECT bl.bId AS bId, u.custno as 用户编号,u.name as 用户名称, u.sex as 性别, concat_ws('-',case when a.name!='' then a.name else '[无对象]' end , case when b.name!='' then b.name else '[无对象]' end,case when r.name!='' then r.name else '[无对象]' end) as 用户地址,to_char(bl.charge, '0.00') AS 收费金额, to_char(bl.currentBalance, '0.00') AS 当前余额, to_date(to_char(bl.optTime,'YYYY-MM-DD hh:mm:ss'),'YYYY-MM-DD hh:mm:ss') AS 收费时间,case when bl.optMan='sys' then '系统' end ,case when length(o.operator_name)>0 then o.operator_name end, case when o.operator_name!=null then bl.optMan end AS 操作人,u.mobil as 手机号码, u.tel as 联系电话, u.bank as 开户银行,u.account as 银行账户 FROM ((((ha_billRecords bl LEFT JOIN ha_operator o ON bl.optMan=o.operator_code) LEFT JOIN ha_custom u ON u.custid=bl.custid) LEFT JOIN ha_room r ON r.custid=u.custid) LEFT JOIN ha_building b ON r.buildingid=b.buildingid) LEFT JOIN ha_area a ON b.areaid=a.areaid WHERE (bl.operation='收费' OR bl.operation='恢复收费')
AND a.areaid IN #{HaBillrecords.nodeIds}
AND POSITION(#{HaBillrecords.nodeIds} in Left(a.areaNo, 1))>0
and bl.optTime between date(#{HaBillrecords.kaiShi,jdbcType=TIMESTAMP}) and date(#{HaBillrecords.JieShu,jdbcType=TIMESTAMP})
ORDER BY u.custno,to_char(bl.optTime, 'YYYY-MM-DD hh:mm:ss') limit #{page} OFFSET #{rowNum}

mapper.insert:
INSERT INTO ha_billRecords (custId,operation,charge,currentBalance,optTime,optMan)
SELECT cu.custid,#{optType},#{charge}, cu.balance, now(),#{optMan}
FROM ha_custom cu WHERE cu.custid=#{custId}

mapper.select:
SELECT b.bId AS bId , b.operation AS 操作, to_char(b.charge, '0.00') AS 金额, to_date(to_char(b.optTime,'YYYY-MM-DD hh:mm:ss'),'YYYY-MM-DD hh:mm:ss') AS 操作时间, to_char(b.currentBalance, '0.00') AS 当前余额, case when b.optMan = 'sys' then '系统' when length(o.operator_name)>0 then o.operator_name when o.operator_name is null then b.optMan else 'true' end as 操作人 FROM ha_billRecords b LEFT JOIN ha_operator o ON b.optMan=o.operator_code
AND b.custid = ${HaBillrecords.custId}
AND POSITION('${HaBillrecords.optType}' in b.operation)>0
and b.optTime between date(#{HaBillrecords.kaiShi,jdbcType=TIMESTAMP}) and date(#{HaBillrecords.JieShu,jdbcType=TIMESTAMP})
ORDER BY to_char(b.optTime, 'YYYY-MM-DD hh:mm:ss') DESC limit #{page} OFFSET #{rowNum}
