mapper.select:
SELECT t1.itemid , mi.用户号 AS userNo , mi.用户名 AS userName , mi.用户地址 AS userDs , trim(to_char(mi.表号, '9999999999999999')) AS uNo , t1.bcds , t1.scds , t1.csyl , t1.bzt , t1.bccbTime , t1.sccbTime FROM hav_meterinfo mi LEFT JOIN ( SELECT rc1.itemid AS itemid , rc1.meterid AS meterid , rc1.th_number AS bcds , rc1.lf_number AS scds , rc1.delta AS csyl , rc1.state AS bzt , to_char(rc1.th_time, 'yyyy-mm-dd hh24:mi:ss') AS bccbTime ,to_char(rc1.lf_time, 'yyyy-mm-dd hh24:mi:ss') AS sccbTime FROM ha_records rc1 WHERE rc1.cmdid IN ${id}
UNION ALL SELECT er1.itemid AS itemid , er1.meterid AS meterid , er1.n AS bcds , 0 AS scds , 0 AS csyl , er1.state AS bzt , to_char(er1.readtime, 'yyyy-mm-dd hh24:mi:ss') AS bccbTime , '' AS sccbTime FROM ha_errRecord er1 WHERE er1.cmdid IN ${id} ) t1 ON t1.meterid=mi.meterid where 1=1
and mi.areaid IN ( SELECT a.areaid FROM ha_rgn n, ha_area a WHERE n.id=Left(a.areaNo, 1) and #{ids} = n.id)
and mi.areaid IN ${id}

mapper.update:
UPDATE ha_meter  SET th_number=#{thNumber},th_r_time=#{thRTime},s_number=#{thNumber}-lf_number,
state=#{state},fday=to_date(#{thRTime},'yyyy-mm-dd')-1,fmon=to_date(#{thRTime},'yyyy-mm-dd')-CAST(date_part('day', current_date)  as  INTEGER),
fday_n=#{thNumber},fmon_n=#{thNumber}
WHERE meterid=#{meterId}

mapper.select:
SELECT m.meterid as meterId,
mi.用户号 AS userNo,
mi.用户名 AS userName,
mi.用户地址 AS userDs,
m.meterNumber AS meterNumber
from (ha_meter m LEFT JOIN hav_meterinfo mi ON m.meterid=mi.meterid)
LEFT JOIN ha_area a ON m.areaid=a.areaid WHERE m.meterid>0

    and m.collectorid=#{collectorid}


    AND a.areaid=#{areaId}

order by mi.用户地址
