mapper.insert:
INSERT INTO ha_billRecords (custId,operation,charge,currentBalance,optTime,optMan)
SELECT cu.custid,#{optType},#{charge}, cu.balance, now(),#{optMan}
FROM ha_custom cu WHERE cu.custid=#{custId}

mapper.select:
select * from ( SELECT b.bId AS bId , b.operation AS operaTion, trim(to_char(b.charge::real, 'FM999999999.00')) AS charge, trim(to_char(b.currentBalance::real, 'FM999999999.00')) AS currentBalance, to_char(b.optTime, 'yyyy-mm-dd hh24:mi:ss') AS optTime, CASE WHEN b.optMan='sys' THEN '系统' WHEN length(o.operator_name)>0 THEN o.operator_name WHEN o.operator_name IS NULL THEN b.optMan end AS caoZuoRen FROM ha_billRecords b LEFT JOIN ha_operator o ON b.optMan=o.operator_code
AND b.custid = ${HaBillrecords.custId}
AND POSITION('${HaBillrecords.optType}' in b.operation)>0
and to_char(b.optTime, 'YYYY-MM-DD hh:mm:ss') between '${HaBillrecords.kaiShi}' and '${HaBillrecords.JieShu}'
) t ORDER BY 操作时间 DESC
