mapper.select:
SELECT trim(to_char(r.roomid, '99999999'))||'_'|| trim(CASE WHEN m.meterid IS NULL THEN '' ELSE trim(to_char(m.meterid, '99999999')) END) AS roommeterid, cu.custno AS custNo, cu.name as cname, a.name||'-'||b.name||'-'||r.name AS uname, r.description AS description , m.meterNumber AS meterNumber, m.th_number as thNumber, m.lf_number as lfNumber, m.s_number as sNumber, m.state as state, to_char(m.th_r_time, 'yyyy-mm-dd hh24:mi:ss') as thRTime, p.name as pname, CASE WHEN c.addr != '' then c.addr ELSE '[无对象]' END AS addr, c.id AS id, CASE WHEN co.addr != '' THEN co.addr||'('||to_hex(co.nconf)||')'ELSE '[无对象]' END AS caddr, m.g_number AS gNumber, m.offset_n AS offsetN, m.vendor_id AS vendorId, m.meter_channel AS meterChannel, m.meter_sequence AS meterSequence,to_char(m.starttime, 'yyyy-mm-dd') as startTime FROM ((((((ha_room r LEFT JOIN ha_custom cu ON r.custid=cu.custid) LEFT JOIN ha_meter m ON m.roomid=r.roomid) LEFT JOIN ha_centor c ON c.id=m.centorid) LEFT JOIN ha_collector co ON co.collectorid=m.collectorid) LEFT JOIN ha_pricestandard p ON m.pricestand_id=p.pricestand_id) LEFT JOIN ha_building b ON r.buildingid=b.buildingid) LEFT JOIN ha_area a ON a.areaid=b.areaid WHERE r.roomid>0
and a.areaid=#{haRoom.areaid}
and r.buildingid=#{haRoom.buildingId}
and (a.registeredNo like '%${haRoom.keyWord}%' or cu.name like '%${haRoom.keyWord}%' or a.areaNo like '%${haRoom.keyWord}%')
ORDER BY a.name||'-'||b.name||'-'||r.name limit #{page} OFFSET #{rowNum}
