SELECT p.billid as pbillid , mi.用户号 as 用户编号 , mi.用户名 as 用户名称 , mi.用户地址 as 用户地址 , p.state as pstate , to_char(p.plantime, 'YYYY-MM-DD hh24:mm:ss') as pplantime , ps.name AS psname , f.delta AS fdelta , p.sum as psum
, COALESCE(p.lastarrear, 0) as plastarrear , to_char(p.mbtime, 'YYYY-MM-DD hh24:mm:ss') as pmbtime FROM ((ha_paylog p LEFT JOIN ha_freeze f ON p.billid=f.billid) LEFT JOIN hav_meterinfo mi ON f.meterid=mi.meterid) LEFT JOIN ha_priceStandard ps ON f.pricestand_id=ps.pricestand_id WHERE p.billid is not null
and state = #{HaPaylog.state}
and mi.areaid in
#{item}
and to_char(mbtime,'YYYY-mm-dd hh24:mm:ss') between #{HaPaylog.haPricestandard.startTime,jdbcType=CHAR} and
#{HaPaylog.haPricestandard.endTime,jdbcType=CHAR}
limit #{page} OFFSET #{rowNum}

        SELECT  count(1)
        FROM ((ha_paylog p LEFT JOIN ha_freeze f ON p.billid=f.billid)
		 LEFT JOIN hav_meterinfo mi ON f.meterid=mi.meterid)
		 LEFT JOIN ha_priceStandard ps ON f.pricestand_id=ps.pricestand_id
                                 WHERE p.billid is not null


